//Import React
import React, { Component } from 'react';
import { Platform, View, Text } from 'react-native';
import Constants from 'expo-constants';
import * as Location from "expo-location";
import * as Permissions from 'expo-permissions';
//Import Commands
import { StatusBar } from 'expo-status-bar';
//Import Files
import styles from "./Styles";
import { HunterButton } from './Hunter';
import { HuntedButton } from './Hunted';



// MAIN //
export default class App extends Component {
  state = {
    location: null,
    errorMessage: null,
    data: 'no data...',
    address: 'loading....'
  };

  componentWillMount() {
    if (Platform.OS === 'android' && !Constants.isDevice) {
      this.setState({
        errorMessage: 'Oops, this will not work on Sketch in an Android emulator. Try it on your device!',
      });
    } else {

      this._getLocationAsync();
    }
  }

  _getLocationAsync = async () => {
    let { status } = await Permissions.askAsync(Permissions.LOCATION);
    if (status !== 'granted') {
      this.setState({
        errorMessage: 'Permission to access location was denied',
      });
    }

    let location = await Location.getCurrentPositionAsync({});
    let data = Location.reverseGeocodeAsync(location).then(d => {
      this.setState({ data: d });
    })
    this.setState({ data });

    let geocode = await Location.reverseGeocodeAsync({ latitude: location.coords.latitude, longitude: location.coords.longitude });
    console.log("Geocode:");
    this.setState({ data: geocode });
    console.log(geocode);

    this.setState({ location });
    this.setState({ data });
  };

  render() {
    let text = 'Waiting..';
    let data = 'loading';
    if (this.state.errorMessage) {
      text = this.state.errorMessage;
    } else if (this.state.location) {
      text = JSON.stringify(this.state.location);
      data = JSON.stringify(this.state.data);
    }

    return (
      <View style={styles.body}>
        <Text style={styles.text}>{text}</Text>
        <Text style={styles.text}>{data}</Text>
        <Text style={styles.text}>{this.state.address}</Text>
      </View>

    );
  }
}



