//Import React
import React, { Component } from 'react';
import { View, Text, Button } from 'react-native';
import Location from 'react-native-location';
//Import Expo
import { StatusBar } from 'expo-status-bar';
//Import Files
import styles from "./Styles";
import { HunterButton } from './Hunter';
import { HuntedButton } from './Hunted';



// MAIN //
export default App => {

  Location.configure({
    distanceFilter: 0,
  })

  const permissionHandle = async () => {
    let permission = await Location.checkPermission({
      ios: 'whenInUse', 
      android: {
        detail: 'coarse'
      }
    });
  }

  let location;

  if(!permission) {
      permission = Location.requestPermission({
      ios: "whenInUse",
      android: {
        detail: "coarse",
        rationale: {
          title: "We need access to your location.",
          message: "",
          buttonNegative: "Cancel",
          buttonPositive: "OK"
        }
      }
    })
    location = Location.getLatestLocation({timeout: 100})
  }


  return(
    <View style = { styles.body } >
      <StatusBar style="auto" />

      <View style={styles.content1}>
        <Text style={styles.title}>WorldScape</Text>
      </View>

      <View style={styles.mainButtons}>
        <HunterButton onPress={permissionHandle}/>
        <HuntedButton />
      </View>

    </View>
  ); 
}
