import { StyleSheet, Button } from 'react-native';


//Main theme colors
var lemon = "#EAE2B7";
var maxred = "#D62828";
var prusblue = "#003049";
var orange = "#F77F00";

export default StyleSheet.create({
    body: {
        flex: 1,
        padding: 150,
        flexDirection: 'column',
        alignItems: 'center',
        backgroundColor: lemon,
    },
    title: {
        color: maxred,
        fontSize: 60,
        borderBottomWidth: 1,
        borderBottomColor: maxred,
    },
    content1: {
        flex: 1.5,
        justifyContent: 'center',
    },
    mainButtons: {
        flex: 2,
        width: 150,
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'space-evenly',
    },
    huntedButton: {
        fontSize: 48,
        margin: 36,
        backgroundColor: orange,
        borderColor: prusblue, 
        borderWidth: 4,
        borderRadius: 5,
    },
    hunterButton: {
        fontSize: 48,
        margin: 36,
        backgroundColor: orange,
        borderColor: prusblue, 
        borderWidth: 4,
        borderRadius: 5,
    },
    text: {
        color: prusblue,
        fontSize: 30,
        margin: 10,
    },
    socialButton: {
        width: 100,
        height: 100,
    },
    input: {
        margin: 10,
        borderWidth: 2,
        borderRadius: 5,
    },
});
