//Import React
import React, { Component } from 'react';
import { View, Text, Button } from 'react-native';
import Location from 'react-native-location';
//Import Expo
import { StatusBar } from 'expo-status-bar';
//Import Files
import styles from "./Styles";
import { HunterButton } from './Hunter';
import { HuntedButton } from './Hunted';



// MAIN //
export default class App extends Component {

  state = {
		location: null
	};

	findCoordinates = () => {
		navigator.geolocation.getCurrentPosition(
			position => {
				const location = JSON.stringify(position);

				this.setState({ location });
			},
			error => Alert.alert(error.message),
			{ enableHighAccuracy: true, timeout: 20000, maximumAge: 1000 }
		);
	};

	render() {

    return(
      <View style = { styles.body } >
        <StatusBar style="auto" />

        <View style={styles.content1}>
          <Text style={styles.title}>WorldScape</Text>
        </View>

        <View>
          <Text style={styles.text}>Location: {this.state.location}</Text>
        </View>

        <View style={styles.mainButtons}>
          <HunterButton press="findCoordinates"/>
          <HuntedButton />
          <Button onPress={this.findCoordinates} title></Button>
        </View>

      </View>
    ); 
  }
}
