//Import React
import React, {Component} from 'react';
import Location from 'react-native-location';
import { View, Text } from 'react-native';
//Import Commands
import { StatusBar } from 'expo-status-bar';
//Import Files
import styles from "./Styles";
import { HunterButton } from './Hunter';
import { HuntedButton } from './Hunted';



// MAIN //
export default class App extends Component {



  constructor() {
    super();
    this.state = {
      ready: false,
      where: { lat: null, lng: null },
      error: null
    }
  }
  componentDidMount() {
    let geoOptions = {
      LocationAccuracy: 4,
      timeInterval: 2000,
      distanceInterval: 20,
      mayShowUserSettingDialog: false,
    };
    this.setState({ ready: false, error: null });
    Location.getCurrentPositionAsync(geoOptions, this.geoSuccess);
  }
  geoSuccess = (position) => {
    console.log(position.coords.latitude);

    this.setState({
      ready: true,
      where: { lat: position.coords.latitude, lng: position.coords.longitude }
    })
  }
  geoFailure = (err) => {
    this.setState({ error: err.message });
  }

  render() {
    return (
      <View style={styles.body}>
      <StatusBar style="auto" />

      <View style={styles.content1}>
        <Text style={styles.title}>WorldScape</Text>
      </View>

      <View>
        {!this.state.ready &&
          (<Text style={styles.text}>No known location</Text>)}
        {this.state.ready &&
          (<Text style={styles.text}>Latitude: {this.state.where.lat} Longitude: {this.state.where.lat}</Text>)}
        {this.state.error && 
          (<Text style={styles.text}>{this.state.error}</Text>)}
      </View>

      <View style={styles.mainButtons}>
        <HunterButton />
        <HuntedButton />
      </View>

    </View>
    );
  }
}
